sourcesContent:
const { sign, verify } = require("jsonwebtoken");
module.export={
signAccessToken:(user)=>{
    return new promise((resolve,reject)=>{
         const payload={ }
         const secret="some key"
         const options={
            expiresIn:'1h',
            issuer:'avihka.com',
            audience:user.email,
         }
         sign({ email: user.email }, secret,options,(err,token)=>{

            if(err) return reject(err)
            resolve(token)
         });

    })
} 





}

